import React, { useEffect, useState } from 'react';
import { ScrollView, View, Text, StyleSheet, TextInput, Dimensions, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { LineChart, Grid } from 'react-native-chart-kit';
import Icon from 'react-native-vector-icons/Ionicons';
import axios from 'axios';
import { Linking } from 'react-native';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const StockList = [
  { symbol: 'AAPL', company: 'Apple Inc.', price: '120', percent: '2.6' },
  { symbol: 'GOOG', company: 'Alphabet Inc.', price: '1000', percent: '3.1' },
];

const NewsList = [
  { title: 'News 1', summary: 'This is a summary of news 1' },
  { title: 'News 2', summary: 'This is a summary of news 2' },
];

const AIStockPickList = [
  { symbol: 'AI1', company: 'AI Company 1', price: '250', percent: '5.3' },
  { symbol: 'AI2', company: 'AI Company 2', price: '350', percent: '4.7' },
];

const chartConfig = {
  backgroundGradientFrom: "#1E2923",
  backgroundGradientTo: "#08130D",
  color: (opacity = 1) => `rgba(26, 255, 146, ${opacity})`,
  strokeWidth: 2
};

const HomeScreen = ({ navigation }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    axios.get('http://10.0.2.2:5000/stocks')
      .then(response => {
        setStocks(response.data);
      })
      .catch(error => {
        console.error('There was an error!', error);
      });
  }, []);

  const filteredStocks = stocks.filter(stock =>
    stock.ticker.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <ScrollView style={styles.container}>
      <TextInput
        style={styles.searchBar}
        onChangeText={setSearchTerm}
        value={searchTerm}
        placeholder="Search Stock Symbols"
      />
      {filteredStocks.map((stock, index) => (
        <View style={styles.stockItem} key={index}>
          <Text
            onPress={() => navigation.navigate('Details', {
              symbol: stock.ticker,
              company: stock.ticker,
              price: stock.currentPrice,
            })}
            style={styles.stockText}
          >
            {stock.ticker}
          </Text>
          <Text
            onPress={() => navigation.navigate('Details', {
              symbol: stock.ticker,
              company: stock.ticker,
              price: stock.currentPrice,
            })}
            style={styles.stockText}
          >
            {stock.company}
          </Text>
          <Text
            onPress={() => navigation.navigate('Details', {
              symbol: stock.ticker,
              company: stock.ticker,
              price: stock.currentPrice,
            })}
            style={styles.stockText}
          >
            ${stock.currentPrice}
          </Text>
        </View>
      ))}
    </ScrollView>
  );
};

const NewsDetailsScreen = ({ route, navigation }) => {
  const { title, summary } = route.params;

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.text}>{summary}</Text>
      <Text style={styles.text}>Here you will add more detailed info</Text>
    </ScrollView>
  );
};

const DetailsScreen = ({ route, navigation }) => {
  const { symbol, company, price, percent } = route.params;

  const data = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
    datasets: [
      {
        data: [50, 10, 40, 95, -4, 24, 85],
        color: (opacity = 1) => `rgba(134, 65, 244, ${opacity})`,
        strokeWidth: 2
      }
    ]
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <LineChart
        data={data}
        width={Dimensions.get("window").width - 30}
        height={300}
        chartConfig={chartConfig}
        bezier
        style={styles.largeChart}
      />

      <Text style={styles.title}>{symbol}</Text>
      <Text style={styles.subtitle}>{`$${price}`}</Text>
      <Text style={styles.text}>High: $150.00</Text>
      <Text style={styles.text}>Low: $120.00</Text>
      <Text style={styles.text}>{`${percent}% increase in stock`}</Text>
      <Text style={styles.text}>Here you will add more detailed info</Text>
    </ScrollView>
  );
};

const ProfileScreen = ({ navigation }) => {
  const [stocks, setStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    axios.get('http://10.0.2.2:5000/stocks')
      .then(response => setStocks(response.data))
      .catch(error => console.error(`Error: ${error}`));
  }, []);

  const filteredStocks = stocks.filter(stock =>
    stock.ticker.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getRecommendationColor = (recommendation) => {
    switch (recommendation.toLowerCase()) {
      case 'buy':
        return 'green';
      case 'strong_buy':
        return 'black';
      case 'hold':
        return 'orange';
      case 'sell':
        return 'red';
      default:
        return 'black';
    }
  };

  return (
    <ScrollView style={styles.container}>
      <TextInput
        style={styles.searchBar}
        onChangeText={setSearchTerm}
        value={searchTerm}
        placeholder="Search Stock Symbols"
      />
      {filteredStocks.map((stock, index) => (
        <TouchableOpacity
          key={index}
          onPress={() => navigation.navigate('Details', {
            symbol: stock.ticker,
            company: stock.ticker,
            price: stock.currentPrice,
          })}
          style={styles.stockItem}

>
          <Text
            style={styles.stockText}
          >
            {stock.ticker}
          </Text>
          <Text style={styles.stockText}>
            ${stock.currentPrice}
          </Text>
          <Text
            style={{
              ...styles.stockText,
              color: getRecommendationColor(stock.recommendationKey),
              fontWeight: 'bold',
              textTransform: 'uppercase'
            }}
          >
            {stock.recommendationKey}
          </Text>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
};

const NewsScreen = ({ navigation }) => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    axios.get('http://10.0.2.2:5000/news')
      .then(response => {
        setNews(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleLink = (url) => {
    Linking.canOpenURL(url).then(supported => {
      if (supported) {
        Linking.openURL(url);
      } else {
        console.log(`Don't know how to open URL: ${url}`);
      }
    });
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {news.map((newsItem, index) => (
        <TouchableOpacity
          key={index}
          onPress={() => handleLink(newsItem.link)}
          style={styles.stockItem}
        >
          <Text style={styles.stockText}>{newsItem.title}</Text>
          <Text style={styles.stockText}>{newsItem.summary}</Text>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
};

const HomeStack = () => (
  <Stack.Navigator>
    <Stack.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
    <Stack.Screen name="Details" component={DetailsScreen} options={{ headerShown: false }} />
  </Stack.Navigator>
);

const App = () => (
  <NavigationContainer>
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === 'Home') {
            iconName = focused ? 'home' : 'home-outline';
          } else if (route.name === 'News') {
            iconName = focused ? 'newspaper' : 'newspaper-outline';
          } else if (route.name === 'My AI') {
            iconName = focused ? 'person' : 'person-outline';
          }

          return <Icon name={iconName} size={size} color={color} />;
        },
      })}
      tabBarOptions={{
        activeTintColor: 'blue',
        inactiveTintColor: 'gray',
      }}
    >
      <Tab.Screen name="Home" component={HomeStack} />
      <Tab.Screen name="News" component={NewsScreen} />
      <Tab.Screen name="My AI" component={ProfileScreen} />
    </Tab.Navigator>
  </NavigationContainer>
);

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#f5f5f5',
  },
  stockItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: 50,
    width: '90%',
    borderColor: 'black',
    borderWidth: 1,
    borderRadius: 10,
    margin: 10,
    padding: 10,
  },
  stockText: {
    fontSize: 20,
  },
  searchBar: {
    marginBottom: 20,
    paddingHorizontal: 20,
    paddingVertical: 10,
    backgroundColor: '#fff',
    borderRadius: 10,
  },
  largeChart: {
    height: Dimensions.get('window').height / 2,
    width: Dimensions.get('window').width - 40,
    marginBottom: 25,
    marginTop: 25,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 20,
  },
  text: {
    fontSize: 16,
    color: '#333',
    marginBottom: 10,
  },
  largeChart: {
    marginVertical: 8,
    borderRadius: 16,
  },
});

export default App;
